sqlQuery.sql:
select source_id ,project_id ,gene_source_id ,matched_result ,10 as wdk_weight ,listagg(pubmed_id,',') within group(order by pubmed_id) as pubmed_id
from( select distinct source_id ,project_id ,gene_source_id ,matched_result ,10 as wdk_weight ,pubmed_id from ( select distinct ta.source_id , ta.project_id , 'Y' as matched_result ,ta.gene_source_id ,oen.name as e_name ,oqn.name as q_name ,pm.pubmed_id from APIDB.PHENOTYPEMODEL pm , ApidbTuning.TranscriptAttributes ta , APIDB.PHENOTYPERESULT pr , sres.ontologyterm oen , sres.ontologyterm oqn , APIDB.NAFEATUREPHENOTYPEMODEL na where ta.gene_na_feature_id = na.na_feature_id and ta.organism = '$$organismwithPhenotype$$' and pm.phenotype_model_id = na.phenotype_model_id and pm.phenotype_model_id = pr.phenotype_model_id and pr.PHENOTYPE_ENTITY_TERM_ID = oen.ontology_term_id (+) and pr.phenotype_quality_term_id = oqn.ontology_term_id (+)
) where e_name = '$$phenotype$$' and q_name = '$$phenotypeQualifier$$' ) group by source_id,project_id,gene_source_id,matched_result
